/**
 * 獲取學校的 CHSC 資料（合併多個資料來源）
 * @param schoolName 學校名稱
 * @returns CHSC 資料，若無則返回 undefined
 *
 * 優先順序：
 * 1. CHSC 資料（官方 PSP/SSP，涵蓋小學和中學）
 * 2. EDB 資料（涵蓋範圍廣，包含幼稚園電話和傳真）
 * 3. Schooland Email（幼稚園/小學/中學電郵）
 * 4. Manual Email（特殊學校、國際學校等手動添加）
 */
export function getCHSCData(schoolName: string): CHSCSchoolData | undefined {
  const chsc = chscDataMap[schoolName];
  const edb = getEDBContactData(schoolName);
  const kgEmail = getKGEmail(schoolName);
  const psSsEmail = getPsSsEmail(schoolName);
  const manualEmail = getManualEmail(schoolName);

  // If no data from any source
  if (!chsc && !edb && !kgEmail && !psSsEmail && !manualEmail) return undefined;

  // Merge data: CHSC > EDB > schooland > manual
  return {
    phone: chsc?.phone || edb?.phone,
    fax: chsc?.fax || edb?.fax,
    email: chsc?.email || kgEmail || psSsEmail || manualEmail,
    yearEstablished: chsc?.yearEstablished,
    schoolMotto: chsc?.schoolMotto,
  };
}

/**
 * 檢查學校是否有 CHSC 資料
 * @param schoolName 學校名稱
 */
export function hasCHSCData(schoolName: string): boolean {
  return schoolName in chscDataMap || !!getEDBContactData(schoolName);
}
